/**
 * @description
 *
 * Function that returns the ComponentStore class registered as a provider, and uses a factory provider to instantiate the ComponentStore and run the lifecycle hooks defined on the ComponentStore.
 *
 * @param componentStoreClass - The ComponentStore with lifecycle hooks
 *
 * @returns Provider[]
 *
 * @usageNotes
 * ```ts
 * @Injectable()
 * export class MyStore
 *    extends ComponentStore<{ init: boolean }>
 *    implements OnStoreInit, OnStateInit
 *   {
 *
 *   constructor() {
 *     super({ init: true });
 *   }
 *
 *   ngrxOnStoreInit() {
 *     // runs once after store has been instantiated
 *   }
 *
 *   ngrxOnStateInit() {
 *     // runs once after store state has been initialized
 *   }
 * }
 *
 * @Component({
 *   providers: [
 *     provideComponentStore(MyStore)
 *   ]
 * })
 * export class MyComponent {
 *   constructor(private myStore: MyStore) {}
 * }
 * ```
 *
 */

docs.usageNotes:
```ts
@Injectable()
export class MyStore
   extends ComponentStore<{ init: boolean }>
   implements OnStoreInit, OnStateInit
  {

  constructor() {
    super({ init: true });
  }

  ngrxOnStoreInit() {
    // runs once after store has been instantiated
  }

  ngrxOnStateInit() {
    // runs once after store state has been initialized
  }
}

@Component({
  providers: [
    provideComponentStore(MyStore)
  ]
})
export class MyComponent {
  constructor(private myStore: MyStore) {}
}
```
