```typescript
describe('Books Effects', () => {
  let store: MockStore;

  beforeEach(() => {
    store = createMockStore({
      initialState: { books: { entities: ['Book 1', 'Book 2', 'Book 3'] } },
      selectors: [
        { selector: selectAllBooks, value: ['Book 1', 'Book 2'] },
        { selector: selectVisibleBooks, value: ['Book 1'] },
      ],
    });
  });
});
```
